@namespace
class SpriteKind:
    boss = SpriteKind.create()
    princess = SpriteKind.create()
    barril = SpriteKind.create()

def on_down_pressed():
    mario.set_image(assets.image("""
        1
    """))
controller.down.on_event(ControllerButtonEvent.PRESSED, on_down_pressed)

def on_right_pressed():
    mario.set_image(assets.image("""
        1
    """))
controller.right.on_event(ControllerButtonEvent.PRESSED, on_right_pressed)

def on_left_pressed():
    mario.set_image(assets.image("""
        marioL
    """))
controller.left.on_event(ControllerButtonEvent.PRESSED, on_left_pressed)

def on_up_pressed():
    if mario.is_hitting_tile(CollisionDirection.BOTTOM):
        mario.vy = -150
        if controller.left.is_pressed():
            mario.set_image(assets.image("""
                mJumpL
            """))
        if controller.right.is_pressed():
            mario.set_image(assets.image("""
                mJumpR
            """))
controller.up.on_event(ControllerButtonEvent.PRESSED, on_up_pressed)

barrilP: Sprite = None
barrilR: Sprite = None
mario: Sprite = None
mario = sprites.create(assets.image("""
    1
"""), SpriteKind.player)
mario.set_position(6, 615)
controller.move_sprite(mario, 80, 0)
mario.ay = 500
scene.set_background_image(assets.image("""
    hm
"""))
tiles.set_current_tilemap(tilemap("""
    level1
"""))
scene.set_background_color(15)
scene.camera_follow_sprite(mario)
kong = sprites.create(assets.image("""
    2
"""), SpriteKind.boss)
kong.set_position(395, 100)
peach = sprites.create(assets.image("""
    3
"""), SpriteKind.princess)
peach.set_position(300, 70)
game.show_long_text("Bienvenido a Donkey Kong! (Sí...Esto es un Donkey Kong...)",
    DialogLayout.BOTTOM)
game.show_long_text("Este fue el primer juego de Donkey Kong, salido en el año 1981, también fue la primera aparición de el fontanero más conocido del planeta.",
    DialogLayout.BOTTOM)
game.show_long_text("Este juego intenta recrear la experiencia exacta de ese clasico, esperamos qe lo disfrutes.",
    DialogLayout.BOTTOM)

def on_forever():
    global barrilR
    for index in range(999999):
        pause(3000)
        barrilR = sprites.create(assets.image("""
            barril1
        """), SpriteKind.barril)
        tiles.place_on_random_tile(barrilR, sprites.dungeon.collectible_red_crystal)
        barrilR.set_velocity(-50, 0)
        barrilR.ay = 500
forever(on_forever)

def on_forever2():
    global barrilP
    for index2 in range(999999):
        pause(3000)
        barrilP = sprites.create(assets.image("""
            barril2
        """), SpriteKind.projectile)
        tiles.place_on_random_tile(barrilP, sprites.dungeon.collectible_blue_crystal)
        barrilP.set_velocity(50, 0)
        barrilP.ay = 500
forever(on_forever2)
